import React,{useEffect,useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { Height } from '@material-ui/icons';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
   
   
    height:"84%",
    width:"90%",
    backgroundColor:"#39445a",
    border:"1px solid #282c34",
    borderRadius:12,
    color:"white",
    boxShadow:theme.shadows[5],
    padding: theme.spacing(1,1,3)

  },
})); 

export default function ContentModel({children,media_type,id}) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
 
  const [content, setcontent] = useState();
  const [video, setVideo] = useState();


  const fetchData=async()=>
  {
     
    const {data}= await axios.get(`https://api.themoviedb.org/3/${media_type}/${id}?api_key=eb174133a332dcdc4dbaf12f0723b142&language=en-US`)
     console.log(data);
    setcontent(data);
  }


  const fetchVideo=async()=>
  {
    //   const {Vdata}=await axios.get(`https://api.themoviedb.org/3/${media_type}/${id}/video?api_key=eb174133a332dcdc4dbaf12f0723b142&language=en-US`)

    //   console.log(Vdata);
    // setVideo(Vdata.results[0]?.key);
    }

    useEffect(() => {
        fetchData();
        fetchVideo();
    }, [])

  return (
    <div>
      <button type="button" className="media" onClick={handleOpen}>
        {children}
      </button>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <h2 id="transition-modal-title">Transition modal</h2>
            <p id="transition-modal-description">react-transition-group animates me.</p>
          </div>
        </Fade>
      </Modal>
    </div>
  );
}
