import axios from 'axios'
import React,{useState,useEffect} from 'react'
import { CustomPagination } from '../../Pagination/CustomPagination'
import SingleContent from '../Treading/SingleContent'
export default function Movies() {

    const api="eb174133a332dcdc4dbaf12f0723b142"
    const [page, setPage] = useState(1);
    const [content, setcontent] = useState([]);
    const [no_pages, setno_pages] = useState(10)

    useEffect(() => {
      
        fetchMovies();
    }, [page])

    const fetchMovies=async()=>
    {
       
        
        const {data}=await axios.get(` https://api.themoviedb.org/3/discover/movie?api_key=eb174133a332dcdc4dbaf12f0723b142&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=${page}`)
        // setcontent(data.results);
     
        // console.log(data.results);
        setcontent(data.results);
        setno_pages(data.total_pages);
        console.log(no_pages)

    

    }

    return (
     
        <div>
            
        <span className="pageTitle"> Movies
        </span>
        
        <div className="trending">
        {
        content&& content.map((c)=><SingleContent
        
        
        key={c.id} id={c.id} title={c.title||c.name} poster={c.poster_path} date={c.release_date||c.first_air_date}  media_type={c.media_type} vote1={c.vote_average}
        />)}
        
        
        </div>
        <CustomPagination setPage={setPage} numberPages={no_pages}/>
        
        </div>
    )
}
