import { createMuiTheme, Tab, TextField,ThemeProvider } from '@material-ui/core'
import { Search } from '@material-ui/icons'
import Paper from '@material-ui/core/Paper';
import Tabs from '@material-ui/core/Tabs';
import { Button } from '@material-ui/core';
import React ,{useEffect,useState} from 'react';
import SearchIcon from '@material-ui/icons/Search';
import axios from 'axios';


const Search1=()=>{

    const [type, settype] = useState(0);
    const [page, setPage] = useState(1);
    const [SearchText, setSearchText] = useState("");
    const [content, setcontent] = useState();
    const [noPages, setnoPages] = useState();


    const Dtheme=createMuiTheme(
        {
            palette:
            {
                type:'dark',
                main:'#fff'
            }
        }
    )


    const fetchContent=async()=>
    {
const {data}= await axios.get(`
https://api.themoviedb.org/3/search/${type ? "movie" : "tv"}?api_key=eb174133a332dcdc4dbaf12f0723b142&language=en-US&query=${SearchText}&page=${page}&include_adult=true`)


setcontent(data.results);
setnoPages(data.total_pages);

console.log(` the serach data `);
console.log(data.results)

    };

    useEffect(() => {
        fetchContent();
    }, [page,SearchText,noPages,type])


    return (
        <div>
            <ThemeProvider theme={Dtheme}>
            <div style={{display:'flex'}}>
            <TextField
             id="outlined-Success"
            style={{flex:1}}
            className="serchB"
            color="Success"
            label="search"
            variant="outlined"
            onChange={(e)=>setSearchText(e.target.value)}
            
            />
            <Button variant="contained" size="small" style={{marginLeft:12,marginTop:5}} ><SearchIcon/>Find</Button>

            </div>


<br></br>
             <Tabs 
             value={type}
            //  onChange={handleChange}
             indicatorColor="secondary"
             textColor="secondary"
             centered

             onChange={(event,newValue)=>
            {
                settype(newValue);
                setPage(1)

            }}

      >
        <Tab style={{width:"50%"}} label="Movies" />
        <Tab  style={{width:"50%"}}  label="TV Series" />
      </Tabs>
            </ThemeProvider>

            
        </div>
    )
}

export default Search1;
