import React from 'react'
import Pagination from '@material-ui/lab/Pagination';

export const  CustomPagination= ({setPage,numberPages=10}) => {
 const handlePageChange=(page)=>
 {
     setPage(page);
     window.scroll(0,0);
 }

    return (
        <div>
       
          <Pagination
            onChange={(e) => handlePageChange(e.target.textContent)}
            count={numberPages}
            color="primary"
          />
       
      </div>
    )
}
