    type,
    SelectedGenres,
     genres, 
     setGenres, 
     setSelectedGenres, 
     setPage,



}) => {


    const fetchGenres=async()=>
    {
      const {data}=  await axios.get(`https://api.themoviedb.org/3/genre/movie/list?api_key=eb174133a332dcdc4dbaf12f0723b142&language=en-US`)

setGenres(data.genres);
    };

    console.log(genres);

    useEffect(() => {
        fetchGenres();


        return ()=>
        {
            setGenres({});
        }
    }, []);

    return (
        <div style={{padding:"6px 0"}} >
       
      {genres && genres.map((g)=><Chip label={g.name}/>)}

        </div>
    )
}
